INSERT INTO `ct_document_extend_article` VALUES ('4', '', '', '<h3>\r\n	什么是帐号互通？\r\n</h3>\r\n<h3>\r\n	答：所有的在一玩速平台注册的帐号， 在完善个人信息以后，绑定的QQ邮箱或者手机号码均可同时登录一玩速网站\r\n</h3>\r\n<h3>\r\n	帐号互通有什么好处？\r\n</h3>\r\n<h3>\r\n	答：第一，方便帐号记忆，让登录不再繁琐\r\n</h3>\r\n<h3>\r\n	第二，方便修改密码，找回密码服务，手机、邮箱绑定后均可一键找回\r\n</h3>\r\n<h3>\r\n	第三，不再按照别人制定的路线行驶，可以根据自己的喜好自由绑定，自由制定，自由度更高.\r\n</h3>', '0', '0', '');
INSERT INTO `ct_document_extend_article` VALUES ('72', 'T币,一玩速T币', '什么是T币? \r\n答;T币是一种在一玩速游戏平台可自由支付的虚拟货币。', '<p>\r\n	<span style=\"font-size:14px;\"><strong>1</strong></span><span style=\"color:#000000;font-size:14px;\"><strong>、什么是T币？</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;line-height:1.53846;color:#000000;\"><strong> </strong>T币是一种在一玩速游戏平台可自由支付的虚拟货币，玩家可以使用T币充值到任何一玩速旗下运营的游戏里并获得游戏币。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;font-size:14px;\"><strong>2、T币如何获得？</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;font-family:Arial, Helvetica, sans-serif;font-size:14px;line-height:24px;\">（1）直接充值T币，充值方式有（网银、支付宝、微信等），安全方便，游戏充值一键完成，摆脱繁琐流程；</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#1C1B1B;font-family:Arial, Helvetica, sans-serif;font-size:12px;line-height:24px;white-space:normal;\"><span style=\"color:#000000;font-size:14px;\"> （2）一玩速</span><span style=\"color:#000000;font-family:Arial, Helvetica, sans-serif;font-size:14px;line-height:24px;white-space:normal;\"><span style=\"color:#000000;font-size:14px;line-height:21.5384px;white-space:normal;\">玩</span>会不定期开展游戏充值活动，所有用户只需在限定期间内达到活动要求充值条件，即可获得大量的平台币奖励；</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;font-family:Arial, Helvetica, sans-serif;font-size:14px;line-height:24px;white-space:normal;\">（3）</span><span style=\"color:#000000;font-size:14px;\">完成积分任务，不定期发布</span><span style=\"color:#000000;font-family:Arial, Helvetica, sans-serif;font-size:14px;line-height:24px;white-space:normal;\">推广返利活动，玩家推广的下线进行充值时，将会按其充值金额的10%返回平台币给玩家；</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;font-family:Arial, Helvetica, sans-serif;font-size:14px;line-height:24px;white-space:normal;\"> （4）玩家由于充值过程中，操作错误导致成功付款后但游戏内未得到游戏币，这时将会返回相应的T币至平台账号；</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;font-family:Arial, Helvetica, sans-serif;font-size:14px;line-height:24px;white-space:normal;\"><strong>3、T币兑换比例是多少？</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;line-height:24px;color:#000000;font-family:Arial, Helvetica, sans-serif;\"><strong> </strong>T币与游戏币兑换比例为1：1，也就是1元=1T币。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '0', '0', '');
INSERT INTO `ct_document_extend_article` VALUES ('73', '', '', '<p style=\"margin-bottom:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	<strong><span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">充值流程</span></span></strong> \r\n</p>\r\n<p style=\"margin-bottom:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	<span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">1：打开游戏里面的充值，系统会自动弹出充值链接</span></span> \r\n</p>\r\n<p style=\"margin-bottom:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	<span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">2：打开<span style=\"color:#CC33E5;\">充值中心</span></span></span><span style=\"font-family:宋体;\">进行充值</span> \r\n</p>\r\n<p style=\"margin-bottom:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-bottom:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	<strong><span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">支付方式</span></span></strong> \r\n</p>\r\n<p style=\"margin-bottom:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	<span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">1：网上银行</span></span> \r\n</p>\r\n<p style=\"margin-bottom:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	<span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">2：支付宝</span></span> \r\n</p>\r\n<p style=\"margin-bottom:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	<span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">3：财付通</span></span> \r\n</p>\r\n<p style=\"margin-bottom:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	<span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">4：微信</span></span> \r\n</p>\r\n<p style=\"margin-bottom:0px;padding:0px;color:#666666;font-size:14px;line-height:30px;white-space:normal;\">\r\n	<span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">5：</span><span style=\"font-family:calibri;\">T</span></span><span style=\"font-family:宋体;\">币</span> \r\n</p>\r\n<p style=\"margin-bottom:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	<span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">6：充值卡（神州行、联通卡、电信卡、天宏一卡通、骏网卡、盛大卡、征途卡）</span></span> \r\n</p>\r\n<p style=\"margin-bottom:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	<span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">7：</span><span style=\"font-family:calibri;\"><span style=\"color:#666666;font-family:宋体;font-size:14px;line-height:30px;white-space:normal;\">人工充值</span></span></span> \r\n</p>', '0', '0', '');
INSERT INTO `ct_document_extend_article` VALUES ('74', '', '', '<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	解决方案： 故障排除方法。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	1、网页html的错误\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	从另一个用户帐户，另一个浏览器和另一台计算机测试网页，如果问题只在您查看一个或两个网页时发生，则从另一个用户帐户，另一个浏览器或另一台计算机查看这些网页，以确定问题是否依然存在。如果脚本错误依然存在，则可能是网页的编写有问题。如果从另一个用户帐户使用网页时脚本错误未发生，则继续进行故障排除操作。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	2、 检验ie设置和flash插件版本的问题\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	首先确定flash版本ie版本是否为当前很新的版本。确认无误后，查看ie相关选项设置。确认活动脚本、ActiveX 和 Java 未被阻止。在默认的情况下，Microsoft Windows Server 2003 针对受限站点区域和 Internet 区域使用“高”安全级别。如要为当前网页重置 Internet Explorer 安全设置，请按照下列步骤操作：\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	启动 Internet Explorer。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	在“工具”菜单上，单击“Internet 选项”。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	在“Internet 选项”对话框中，单击“安全”。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	单击“默认级别”。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	单击“确定”。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	3、确认程序中（如防病毒程序或防火墙）未配置为阻止活动脚本、ActiveX 控件或 Java 小程序。请参见您使用的防病毒程序或防火墙的文档资料，以确定如何打开脚本、ActiveX 和Java 小程序。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	4、确认您的防病毒程序未设置为扫描“临时 Internet 文件”或“已下载的程序文件”文件夹\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	5、删除所有临时的 Internet 相关文件\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	从您的计算机中删除所有临时的 Internet 相关文件。为此，请按照下列步骤操作：\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	启动 Internet Explorer。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	在“工具”菜单上，单击“Internet 选项”。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	单击“常规”选项卡。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	在“Internet 临时文件”下，单击“设置”。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	单击“删除文件”。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	单击“确定”。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	单击“删除 Cookies”。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	单击“确定”。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	在“历史记录”下，单击“清除历史记录”，然后单击“是”。\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#666666;font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;white-space:normal;\">\r\n	单击“确定”。\r\n</div>', '0', '0', '');
