INSERT INTO `ct_paytype` VALUES ('1', '人工充值', '人工充值', '_sysrgpay', '0', '#', '0', '0', '', '', '10', '10', '5000', '', '0', '<p style=\"color:#666666;font-size:12px;\">\r\n	<b> </b> \r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-weight:700;color:#EE33EE;\"><span style=\"font-size:10.5pt;color:#EE33EE;\"><span style=\"font-family:宋体;color:#EE33EE;\">1、什么是人工充值？</span></span></span> </b> \r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">人工充值需要您先汇款到我们公司的账号内，核实无误后<span style=\"font-family:calibri;\">1</span></span><span style=\"font-family:宋体;\">分钟内手动给您账号进行充值</span></span> </b> \r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-weight:700;color:#EE33EE;\"><span style=\"font-size:10.5pt;color:#EE33EE;\"><span style=\"font-family:宋体;color:#EE33EE;\">2、充值支持什么银行</span></span></span> </b> \r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">城市商业银行，开户行：泰安银行新泰府前街支行；开户名称：山东雷云网络科技有限公司；帐号：</span><span style=\"font-family:calibri;\"><span style=\"color:#3A4044;font-family:tahoma, arial, \'microsoft yahei\', simsun, sans-serif;font-size:16px;line-height:28px;\">811300101421001519</span></span></span> </b> \r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;color:#666666;font-size:14px;\">\r\n	<b><b></b> </b> \r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-weight:700;color:#EE33EE;\"><span style=\"font-size:10.5pt;color:#EE33EE;\"><span style=\"font-family:宋体;color:#EE33EE;\">3、人工充值流程：</span></span></span> </b> \r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">第<span style=\"font-family:calibri;\">1</span></span><span style=\"font-family:宋体;\">步：请从官网充值中心获取人工充值银行帐号信息。</span></span> </b> \r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">第<span style=\"font-family:calibri;\">2</span></span><span style=\"font-family:宋体;\">步：向官方相应银行帐号转账</span></span><span style=\"font-family:calibri;\">(</span><span style=\"font-family:宋体;\">转账时将金额增加角和分，方便官方充值客服查询</span><span style=\"font-family:calibri;\">)</span> </b> \r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">第<span style=\"font-family:calibri;\">3</span></span><span style=\"font-family:宋体;\">步：及时联系<span style=\"color:#4C33E5;\">在线客服</span>以及拨打客服电话，向充值客服提供准确的游戏帐号</span></span><span style=\"font-family:calibri;\">,</span><span style=\"font-family:宋体;\">游戏名称</span><span style=\"font-family:calibri;\">,</span><span style=\"font-family:宋体;\">游戏服务器</span><span style=\"font-family:calibri;\">,</span><span style=\"font-family:宋体;\">游戏角色</span><span style=\"font-family:calibri;\">,</span><span style=\"font-family:宋体;\">充值银行</span><span style=\"font-family:calibri;\">,</span><span style=\"font-family:宋体;\">充值金额</span><span style=\"font-family:calibri;\">,</span><span style=\"font-family:宋体;\">真实姓名，联系电话等。</span> </b> \r\n</p>\r\n<p style=\"color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">第<span style=\"font-family:calibri;\">4</span></span><span style=\"font-family:宋体;\">步：</span></span><span style=\"font-family:宋体;\">客服人员确认玩家汇款或转帐到账，实时充游戏币。</span> </b> \r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">第<span style=\"font-family:calibri;\">5</span></span><span style=\"font-family:宋体;\">步：请玩家到游戏内查收游戏币；</span></span> </b> \r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-size:10.5pt;color:#CC33E5;\"><span style=\"font-family:宋体;color:#CC33E5;\">4、人工充值注意事项：</span></span></b> \r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">1：人工充值不需要您提供任何密码，请各位玩家提高安全意识，非本页公布的帐号信息均非我平台官方帐号。</span></span> </b> \r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">2：银行汇款或转帐的实到金额享有和网银充值相同的充值比例，异地和跨行汇款或转帐的手续费根据各银行规定由银行向用户收取。</span></span> </b> \r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">3：请汇款或转帐的时候保留单据和转账订单号。</span></span> </b> \r\n</p>\r\n<p style=\"color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">4：如海外汇款的玩家，建议先向当地银行咨询所需资料以后再联系</span><span>一玩速</span></span><span style=\"font-family:宋体;\">客服人员，确认资料正确后再进行汇款。</span> </b> \r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', SimHei, simsun, Arial, Helvetica, sans-serif;color:#666666;font-size:14px;\">\r\n	<b><span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">5：一旦充值成功，系统将不提供充值修正服务，如填写金额错误导致的损失我们不承担！</span></span> </b> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '1434602397', '1434602397', '99', '1');
