INSERT INTO `ct_paytype` VALUES ('2', 'T币', 'T币', '_syspay', '1', '#', '0', '0', '', '', '14', '1', '5000', '', '0', '<p style=\"font-size:13.3333px;\">\r\n	<span style=\"font-size:14px;color:#333333;font-family:SimHei;\"><span style=\"color:#333333;font-family:SimHei;font-size:14px;\">1</span></span><span style=\"color:#333333;font-size:14px;font-family:SimHei;\"><span style=\"color:#333333;font-family:SimHei;font-size:14px;\">、什么是T币？</span></span> \r\n</p>\r\n<p style=\"font-size:13.3333px;\">\r\n	<span style=\"font-size:14px;line-height:1.53846;color:#000000;\"><span></span><span style=\"color:#333333;font-family:SimHei;font-size:14px;\">T币是一种在一玩速游戏平台可自由支付的虚拟货币，玩家可以使用T币充值到任何一玩速旗下运营的游戏里并获得游戏币。</span></span> \r\n</p>\r\n<p style=\"font-size:13.3333px;\">\r\n	<span style=\"color:#333333;font-size:14px;font-family:SimHei;\"><span style=\"color:#333333;font-family:SimHei;font-size:14px;\">2、T币如何获得？</span></span> \r\n</p>\r\n<p style=\"font-size:13.3333px;\">\r\n	<span style=\"color:#333333;font-family:SimHei;font-size:14px;line-height:24px;\">（1）直接充值T币，充值方式有（网银、支付宝、微信等），安全方便，游戏充值一键完成，摆脱繁琐流程；</span> \r\n</p>\r\n<p style=\"font-size:13.3333px;\">\r\n	<span style=\"color:#1C1B1B;font-family:Arial, Helvetica, sans-serif;font-size:12px;line-height:24px;\"><span style=\"color:#333333;font-size:14px;font-family:SimHei;\">（2）</span><span style=\"color:#333333;font-size:14px;line-height:24px;font-family:SimHei;\">一玩速会不定期开展游戏充值活动，所有用户只需在限定期间内达到活动要求充值条件，即可获得大量的平台币奖励；</span></span> \r\n</p>\r\n<p style=\"font-size:13.3333px;\">\r\n	<span style=\"color:#333333;font-family:SimHei;font-size:14px;line-height:24px;\">（3）</span><span style=\"color:#333333;font-size:14px;font-family:SimHei;\">完成积分任务，不定期发布</span><span style=\"color:#333333;font-family:SimHei;font-size:14px;line-height:24px;\">推广返利活动，玩家推广的下线进行充值时，将会按其充值金额的10%返回平台币给玩家；</span> \r\n</p>\r\n<p style=\"font-size:13.3333px;\">\r\n	<span style=\"color:#333333;font-family:SimHei;font-size:14px;line-height:24px;\">（4）玩家由于充值过程中，操作错误导致成功付款后但游戏内未得到游戏币，这时将会返回相应的T币至平台账号；</span> \r\n</p>\r\n<p style=\"font-size:13.3333px;\">\r\n	<span style=\"color:#333333;font-family:SimHei;font-size:14px;line-height:24px;\"><span style=\"color:#333333;font-family:SimHei;font-size:14px;\">3、T币兑换比例是多少？</span></span> \r\n</p>\r\n<p style=\"font-size:13.3333px;\">\r\n	<span style=\"font-size:14px;line-height:24px;color:#000000;font-family:Arial, Helvetica, sans-serif;\"><span></span><span style=\"color:#333333;font-family:SimHei;font-size:14px;\">T币与游戏币兑换比例为1：1，也就是1元=1T币。</span></span> \r\n</p>', '1437136432', '1437136432', '3', '1');
INSERT INTO `ct_paytype` VALUES ('3', '支付宝', '支付宝', 'zfb', '2', '#', '0', '0', '', '', '2', '1', '5000', '', '0', '<ol>\r\n	<li>\r\n		您需要拥有支付宝账户；\r\n	</li>\r\n	<li>\r\n		首次使用需要在手机端下载支付宝钱包；\r\n	</li>\r\n</ol>', '1437192091', '1437192091', '1', '1');
INSERT INTO `ct_paytype` VALUES ('4', '网银', '易宝', 'wyzf', '1', '#', '0', '0', '', '', '12', '1', '5000', '', '1', '<ol style=\"margin-left:20px;color:#999999;font-family:微软雅黑, arial, 宋体;font-size:14px;\">\r\n	<li>\r\n		您必须开通了网上银行业务。\r\n	</li>\r\n	<li>\r\n		网上银行开通办法请咨询当地所属银行。\r\n	</li>\r\n	<li>\r\n		请您关闭所有屏蔽弹出窗口之类的功能，否则在线支付将无法继续，比如：3721、上网助手、google toolbar、alexa toolbar、baidu等。\r\n	</li>\r\n	<li>\r\n		如果您用信用卡支付，请确认该信用卡的网上交易限额大于等于您的充值金额。\r\n	</li>\r\n	<li>\r\n		请充值时务必确认好您的充值金额准确无误后再进行充值，避免输错金额导致的失误，如因未仔细确认金额造成的充值问题，我们将一律不予处理此类退款申诉。\r\n	</li>\r\n</ol>', '1443342612', '1443342612', '2', '1');
